    if (d[ 7 ]) {
        date.setUTCHours(d[ 7 ]);
    }
    else {
        date.setUTCHours(0);
    }
    if (d[ 8 ]) {
        date.setUTCMinutes(d[ 8 ]);
    }
    else {
        date.setUTCMinutes(0);
    }
    if (d[ 10 ]) {
        date.setUTCSeconds(d[ 10 ]);
    }
    else {
        date.setUTCSeconds(0);
    }
    if (d[ 12 ]) {
        date.setUTCMilliseconds(Number("0." + d[ 12 ]) * 1000);
    }
    else {
        date.setUTCMilliseconds(0);
    }

