		"longDateTime3": 1,
		"shortTime": 1,
		"mediumTime": 1,
		"militaryTime": 1,
		"default": 1,
		"shortdatetime": 1,
		"mediumdatetime": 1,
		"longdatetime": 1,
		"timewithseconds": 1,
		"timenoseconds": 1,
		"shortdatetimenosec": 1,
